public class DesafioParvoDoAfonso {

    public static int desafio_parvo (String str) {

        char c = str.charAt(0);
        int index = -1;

        for (int i = 1; i < str.length(); i++) {

            if(str.charAt(i) != c) index = i;
        }

        return index;
    }

    public static void main(String[] args) {
        
        System.out.println(desafio_parvo("............................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................."));
    }
}